ph {\"align\":\"center\",\"textColor\":\"white\"} -->\n<p class=\"has-text-align-center has-white-color has-text-color\">Thanks again Mike!</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:themeisle-blocks/advanced-column --></div></div>\n<!-- /wp:themeisle-blocks/advanced-columns -->\n\n<!-- wp:themeisle-blocks/advanced-columns {\"id\":\"wp-block-themeisle-blocks-advanced-columns-1e057df8\",\"columns\":2,\"layout\":\"oneTwo\",\"layoutMobile\":\"collapsedRows\",\"paddingType\":\"unlinked\",\"paddingTypeMobile\":\"unlinked\",\"paddingTablet\":20,\"paddingMobile\":20,\"paddingTop\":0,\"paddingTopTablet\":20,\"paddingTopMobile\":0,\"paddingRightTablet\":20,\"paddingRightMobile\":0,\"paddingBottom\":40,\"paddingBottomTablet\":20,\"paddingBottomMobile\":0,\"paddingLeftTablet\":20,\"paddingLeftMobile\":20,\"marginTablet\":20,\"marginMobile\":20,\"marginTop\":0,\"marginTopTablet\":20,\"marginTopMobile\":-3,\"marginBottomTablet\":20,\"marginBottomMobile\":20,\"columnsWidth\":1100,\"horizontalAlign\":\"center\",\"backgroundColor\":\"#d7393e\"} -->\n<div class=\"wp-block-themeisle-blocks-advanced-columns has-2-columns has-desktop-oneTwo-layout has-tablet-equal-layout has-mobile-collapsedRows-layout has-default-gap has-vertical-unset\" id=\"wp-block-themeisle-blocks-advanced-columns-1e057df8\"><div class=\"wp-block-themeisle-blocks-advanced-columns-overlay\"></div><div class=\"innerblocks-wrap\"><!-- wp:themeisle-blocks/advanced-column {\"id\":\"wp-block-themeisle-blocks-advanced-column-4809da29\",\"paddingType\":\"unlinked\",\"paddingTypeMobile\":\"unlinked\",\"paddingTablet\":20,\"paddingMobile\":20,\"paddingTopTablet\":20,\"paddingTopMobile\":0,\"paddingRight\":10,\"paddingRightTablet\":20,\"paddingRightMobile\":20,\"paddingBottomTablet\":20,\"paddingBottomMobile\":0,\"paddingLeft\":10,\"paddingLeftTablet\":20,\"paddingLeftMobile\":20,\"marginTablet\":20,\"marginMobile\":20,\"marginTop\":0,\"marginTopTablet\":20,\"marginTopMobile\":0,\"marginRightTablet\":0,\"marginRightMobile\"